import '../styles/Cartelera.css';
import { FaSignOutAlt, FaTicketAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

function Cartelera() {
  const navigate = useNavigate();
  const [peliculas, setPeliculas] = useState([]);

  const cerrarSesion = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('usuario');
    navigate('/login');
  };

  useEffect(() => {
    const obtenerPeliculas = async () => {
      try {
        const res = await axios.get('http://localhost:3000/api/peliculas');
        setPeliculas(res.data.peliculas);
      } catch (error) {
        console.error('Error al cargar las películas:', error);
      }
    };

    obtenerPeliculas();
  }, []);

  return (
          <div className="cartelera-dashboard">
        <aside className="sidebar">
          <h2 className="sidebar-title">Cartelera</h2>
          <nav className="sidebar-menu">
            <a onClick={() => document.getElementById('peliculas').scrollIntoView({ behavior: 'smooth' })}>
              <FaTicketAlt /> Comprar boleto
            </a>
            <a onClick={cerrarSesion}>
              <FaSignOutAlt /> Cerrar Sesión
            </a>
          </nav>
        </aside>

        <main className="cartelera-content">
          <h1 className="cartelera-main-title">Esta es nuestra cartelera</h1>
          <section className="peliculas-grid" id="peliculas">
            {peliculas.length === 0 ? (
              <p>Cargando películas...</p>
            ) : (
              peliculas.map((pelicula) => (
                <div className="pelicula-card" key={pelicula.id}>
                  <img src={pelicula.poster_url} alt={pelicula.titulo} />
                  <div className="pelicula-info">
                    <h3>{pelicula.titulo}</h3>
                    <p>{pelicula.sinopsis}</p>
                  </div>
                </div>
              ))
            )}
          </section>
        </main>
      </div>
  );
}

export default Cartelera;
