import { useState, useEffect } from 'react';
import '../styles/AdminDashboard.css';
import { FaUserCog, FaFilm, FaDoorOpen, FaCalendarAlt, FaClipboardList, FaPlus, FaEdit, FaTrash } from 'react-icons/fa';
import axios from 'axios';

function AdminDashboard() {
  const [seccion, setSeccion] = useState(null);
  const [datos, setDatos] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      if (!seccion) return;

      try {
        const res = await axios.get(`http://localhost:3000/api/${seccion}`);
        setDatos(res.data);
      } catch (error) {
        console.error(`Error al cargar ${seccion}:`, error);
      }
    };

    fetchData();
  }, [seccion]);

  const renderAcciones = (item) => (
    <td className="acciones">
      <button className="btn crear"><FaPlus /></button>
      <button className="btn editar"><FaEdit /></button>
      <button className="btn eliminar"><FaTrash /></button>
    </td>
  );

  const renderUsuarios = () => (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Nombre</th>
          <th>Email</th>
          <th>Tipo</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((u) => (
          <tr key={u.id}>
            <td>{u.id}</td>
            <td>{u.nombre}</td>
            <td>{u.email}</td>
            <td>{u.tipo}</td>
            {renderAcciones(u)}
          </tr>
        ))}
      </tbody>
    </table>
  );

  const renderPeliculas = () => (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Título</th>
          <th>Duración</th>
          <th>Sinopsis</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((p) => (
          <tr key={p.id}>
            <td>{p.id}</td>
            <td>{p.titulo}</td>
            <td>{p.duracion} min</td>
            <td>{p.sinopsis}</td>
            {renderAcciones(p)}
          </tr>
        ))}
      </tbody>
    </table>
  );

  const renderSalas = () => (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Nombre</th>
          <th>Filas</th>
          <th>Columnas</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((s) => (
          <tr key={s.id}>
            <td>{s.id}</td>
            <td>{s.nombre}</td>
            <td>{s.filas}</td>
            <td>{s.columnas}</td>
            {renderAcciones(s)}
          </tr>
        ))}
      </tbody>
    </table>
  );

  const renderFunciones = () => (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Película</th>
          <th>Sala</th>
          <th>Fecha</th>
          <th>Hora</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((f) => (
          <tr key={f.id}>
            <td>{f.id}</td>
            <td>{f.pelicula_id}</td>
            <td>{f.sala_id}</td>
            <td>{f.fecha}</td>
            <td>{f.hora}</td>
            {renderAcciones(f)}
          </tr>
        ))}
      </tbody>
    </table>
  );

  const renderReservaciones = () => (
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Usuario</th>
          <th>Función</th>
          <th>Fila</th>
          <th>Columna</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((r) => (
          <tr key={r.id}>
            <td>{r.id}</td>
            <td>{r.usuario_id}</td>
            <td>{r.funcion_id}</td>
            <td>{r.fila}</td>
            <td>{r.columna}</td>
            {renderAcciones(r)}
          </tr>
        ))}
      </tbody>
    </table>
  );

  const renderContenido = () => {
    switch (seccion) {
      case 'usuarios': return renderUsuarios();
      case 'peliculas': return renderPeliculas();
      case 'salas': return renderSalas();
      case 'funciones': return renderFunciones();
      case 'reservaciones': return renderReservaciones();
      default:
        return (
          <>
            <h1>Panel de Control</h1>
            <p>Selecciona una opción del menú lateral para comenzar.</p>
          </>
        );
    }
  };

  return (
    <div className="admin-dashboard">
      <aside className="sidebar">
        <h2 className="sidebar-title">Administración</h2>
        <nav className="sidebar-menu">
          <a onClick={() => setSeccion('usuarios')}><FaUserCog /> Usuarios</a>
          <a onClick={() => setSeccion('peliculas')}><FaFilm /> Películas</a>
          <a onClick={() => setSeccion('salas')}><FaDoorOpen /> Salas</a>
          <a onClick={() => setSeccion('funciones')}><FaCalendarAlt /> Funciones</a>
          <a onClick={() => setSeccion('reservaciones')}><FaClipboardList /> Reservaciones</a>
        </nav>
      </aside>

      <main className="dashboard-content">
        {renderContenido()}
      </main>
    </div>
  );
}

export default AdminDashboard;
