import { useEffect, useState } from 'react';
import axios from 'axios';
import { Grid, Card, CardContent, CardMedia, Typography, Button, Chip, Container, Box } from '@mui/material';

function Cartelera() {
  const [peliculas, setPeliculas] = useState([]);

  useEffect(() => {
    const obtenerPeliculas = async () => {
      try {
        const res = await axios.get('http://localhost:3000/api/peliculas');
        setPeliculas(res.data.peliculas);
      } catch (error) {
        console.error('Error al cargar las películas:', error);
      }
    };

    obtenerPeliculas();
  }, []);

  return (
    <Container maxWidth="xl" sx={{ paddingTop: '2rem' }}>
      <Typography variant="h3" align="center" gutterBottom>
        Cartelera
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {peliculas.map((pelicula) => (
          <Grid item xs={12} sm={6} md={3} lg={2} key={pelicula.id}>
            <Card sx={{ borderRadius: 3, boxShadow: 4, height: '100%', position: 'relative' }}>
              <CardMedia
                component="img"
                image={pelicula.poster_url}
                alt={pelicula.titulo}
                sx={{ height: 350, objectFit: 'cover' }}
              />

              <Chip
                label="PREVENTA" 
                color="warning"
                sx={{
                  position: 'absolute',
                  top: 16,
                  left: 16,
                  fontWeight: 'bold'
                }}
              />

              <CardContent sx={{ textAlign: 'center' }}>
                <Typography variant="h6" noWrap>
                  {pelicula.titulo}
                </Typography>

                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }} noWrap>
                  {pelicula.sinopsis}
                </Typography>

                <Button variant="contained" color="error" fullWidth>
                  Comprar Entrada
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default Cartelera;
