import '../actions_pages_styles/usuario_eliminar.css';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function UsuarioEliminar() {
  const [usuario, setUsuario] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const obtenerUsuario = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get(`http://localhost:3000/api/usuarios/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsuario(res.data);
    };

    obtenerUsuario();
  }, [id]);

  const handleEliminar = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:3000/api/usuarios/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Usuario eliminado correctamente');
      navigate('/AdminDashboard');
    } catch (error) {
      console.error('Error al eliminar usuario:', error);
      alert('Error al eliminar usuario');
    }
  };

  if (!usuario) return <p className="usuario-eliminar-cargando">Cargando usuario...</p>;

  return (
    <div className="usuario-eliminar-page">
      <div className="usuario-eliminar-card">
        <h2>Eliminar Usuario</h2>
        <p><strong>Nombre:</strong> {usuario.nombre}</p>
        <p><strong>Correo:</strong> {usuario.email}</p>

        <button className="usuario-eliminar-button" onClick={handleEliminar}>
          Confirmar Eliminaci√≥n
        </button>
      </div>
    </div>
  );
}

export default UsuarioEliminar;
