import { useState } from 'react';
import axios from 'axios';
import '../styles/Register.css'; // tu estilo
import { useNavigate } from 'react-router-dom';

function Register() {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [contrasena, setContrasena] = useState('');
  const navigate = useNavigate();

  const handleRegistro = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post('http://localhost:3000/api/usuarios', {
        nombre,
        email,
        contrasena,
      });

      console.log('Usuario creado:', res.data);
      navigate('/login'); // redirigir a login
    } catch (error) {
      console.error('Error en el registro:', error.response?.data || error);
      alert('Hubo un error al registrarse');
    }
  };

  return (
    <div className="register-page">
      <form className="register-card" onSubmit={handleRegistro}>
        <h2>Crear Cuenta</h2>
        <input type="text" placeholder="Nombre" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="ContraseÃ±a" value={contrasena} onChange={(e) => setContrasena(e.target.value)} required />
        <button type="submit">Registrarse</button>
      </form>
    </div>
  );
}

export default Register;
