import '../actions_pages_styles/pelicula_crear.css';
import { FaFilm, FaClock, FaAlignLeft, FaImage } from 'react-icons/fa';
import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function PeliculaCrear() {
  const [titulo, setTitulo] = useState('');
  const [duracion, setDuracion] = useState('');
  const [sinopsis, setSinopsis] = useState('');
  const [poster, setPoster] = useState('');
  const navigate = useNavigate();

  const handleCrear = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:3000/api/peliculas', {
        titulo,
        duracion,
        sinopsis,
        poster
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Película creada correctamente');
      navigate('/AdminDashboard');
    } catch (error) {
      alert('Error al crear película');
    }
  };

  return (
    <div className="form-page">
      <div className="form-card">
        <h2>Crear Película</h2>

        <div className="form-field">
          <FaFilm className="form-icon" />
          <input type="text" placeholder="Título" value={titulo} onChange={(e) => setTitulo(e.target.value)} />
        </div>

        <div className="form-field">
          <FaClock className="form-icon" />
          <input type="number" placeholder="Duración (minutos)" value={duracion} onChange={(e) => setDuracion(e.target.value)} />
        </div>

        <div className="form-field">
          <FaAlignLeft className="form-icon" />
          <textarea placeholder="Sinopsis" value={sinopsis} onChange={(e) => setSinopsis(e.target.value)} />
        </div>

        <div className="form-field">
          <FaImage className="form-icon" />
          <input type="text" placeholder="URL del póster" value={poster} onChange={(e) => setPoster(e.target.value)} />
        </div>

        <button className="form-button" onClick={handleCrear}>Crear Película</button>
      </div>
    </div>
  );
}

export default PeliculaCrear;
