import '../styles/Cartelera.css';
import { FaSignOutAlt, FaTicketAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { Grid, Card, CardContent, CardMedia, Typography, Button, Container, Box } from '@mui/material';

function Cartelera() {
  const navigate = useNavigate();
  const [peliculas, setPeliculas] = useState([]);

  const cerrarSesion = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('usuario');
    navigate('/login');
  };

  useEffect(() => {
    const obtenerPeliculas = async () => {
      try {
        const res = await axios.get('http://localhost:3000/api/peliculas');
        setPeliculas(res.data.peliculas);
      } catch (error) {
        console.error('Error al cargar las películas:', error);
      }
    };

    obtenerPeliculas();
  }, []);

  return (
    <div className="cartelera-dashboard">
      {/* Sidebar intacta */}
      <aside className="sidebar">
        <h2 className="sidebar-title">Cartelera</h2>
        <nav className="sidebar-menu">
          <a onClick={() => document.getElementById('peliculas').scrollIntoView({ behavior: 'smooth' })}>
            <FaTicketAlt /> Comprar boleto
          </a>
          <a onClick={cerrarSesion}>
            <FaSignOutAlt /> Cerrar Sesión
          </a>
        </nav>
      </aside>

      {/* Contenido a la derecha */}
      <main className="cartelera-content">
        <Container maxWidth="xl">
          <Box sx={{ marginBottom: 4 }}>
            <Typography variant="h4" gutterBottom>
              Nuestra Cartelera
            </Typography>
            <Typography variant="body1">
              Elige tu película favorita y compra tus boletos fácilmente.
            </Typography>
          </Box>

          {peliculas.length === 0 ? (
            <Typography variant="body1">Cargando películas...</Typography>
          ) : (
            <Grid container spacing={4} id="peliculas">
              {peliculas.map((pelicula) => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={pelicula.id}>
                  <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', borderRadius: 3, boxShadow: 3 }}>
                    <CardMedia
                      component="img"
                      sx={{ height: 250, objectFit: 'cover' }}
                      image={pelicula.poster_url}
                      alt={pelicula.titulo}
                    />
                    <CardContent sx={{ flexGrow: 1 }}>
                      <Typography variant="h6" gutterBottom>
                        {pelicula.titulo}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                        {pelicula.sinopsis.slice(0, 100)}...
                      </Typography>
                      <Button variant="contained" color="primary" fullWidth>
                        Comprar Boleto
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </Container>
      </main>
    </div>
  );
}

export default Cartelera;
