import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/AdminDashboard.css';
import { FaUserCog, FaFilm, FaDoorOpen, FaCalendarAlt, FaClipboardList, FaPlus, FaEdit, FaTrash } from 'react-icons/fa';
import axios from 'axios';

function AdminDashboard() {
  const navigate = useNavigate();

  const [seccion, setSeccion] = useState(null);
  const [datos, setDatos] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      if (!seccion) return;
  
      try {
        const token = localStorage.getItem('token');
      
        const endpoint = seccion === 'reservaciones'
          ? 'http://localhost:3000/api/reservas/todas'
          : `http://localhost:3000/api/${seccion}`;
      
        const res = await axios.get(endpoint, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
      
        const propiedad = {
          usuarios: 'usuarios',
          peliculas: 'peliculas',
          salas: 'salas',
          funciones: 'funciones',
          reservaciones: 'reservas'
        }[seccion];
      
        const data = res.data[propiedad];
      
        // Aquí está el filtro solo para usuarios
        const datosFiltrados = seccion === 'usuarios'
          ? data.filter((u) => u.activo === 1 || u.activo === true)
          : data;
      
        setDatos(Array.isArray(datosFiltrados) ? datosFiltrados : []);
      } catch (error) {
        console.error(`Error al cargar ${seccion}:`, error);
        setDatos([]);
      } 
    };
  
    fetchData();
  }, [seccion]);

  const renderAcciones = (item) => (
    <td className="acciones">
      {seccion === 'usuarios' && (
        <>
          <button className="btn editar" onClick={() => navigate(`/editar-usuario/${item.id}`)}>
            <FaEdit />
          </button>
          <button
            className="btn eliminar"
            onClick={async () => {
              const confirmado = window.confirm(`¿Estás seguro que deseas eliminar al usuario "${item.nombre}"? Esta acción no se puede deshacer.`);
              if (confirmado) {
                try {
                  const token = localStorage.getItem('token');
                  await axios.delete(`http://localhost:3000/api/usuarios/${item.id}`, {
                    headers: { Authorization: `Bearer ${token}` }
                  });
                  alert('Usuario eliminado correctamente');
                  setDatos((prev) => prev.filter((u) => u.id !== item.id));
                } catch (error) {
                  console.error('Error al eliminar usuario:', error);
                  alert('Hubo un error al intentar eliminar el usuario');
                }
              }
            }}
          >
            <FaTrash />
          </button>
        </>
      )}
    </td>
  );

  const renderUsuarios = () => (
    <>
      <div className="acciones-superiores">
          <button className="btn crear-grande" onClick={() => navigate('/crear-usuario')}>
              <FaPlus /> Crear Usuario
          </button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Email</th>
            <th>Tipo</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {datos.map((u) => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.nombre}</td>
              <td>{u.email}</td>
              <td>{u.tipo}</td>
              {renderAcciones(u)}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );

  const renderPeliculas = () => (
    <>
      <div className="acciones-superiores">
        <button className="btn crear-grande"><FaPlus />   Crear Película</button>
      </div>
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Título</th>
          <th>Duración</th>
          <th>Sinopsis</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((p) => (
          <tr key={p.id}>
            <td>{p.id}</td>
            <td>{p.titulo}</td>
            <td>{p.duracion} min</td>
            <td>{p.sinopsis}</td>
            {renderAcciones(p)}
          </tr>
        ))}
      </tbody>
    </table>
    </>
  );

  const renderSalas = () => (
    <>
      <div className="acciones-superiores">
        <button className="btn crear-grande"><FaPlus />   Crear Sala</button>
      </div>
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Nombre</th>
          <th>Filas</th>
          <th>Columnas</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((s) => (
          <tr key={s.id}>
            <td>{s.id}</td>
            <td>{s.nombre}</td>
            <td>{s.filas}</td>
            <td>{s.columnas}</td>
            {renderAcciones(s)}
          </tr>
        ))}
      </tbody>
    </table>
    </>
  );

  const renderFunciones = () => (
    <>
      <div className="acciones-superiores">
        <button className="btn crear-grande"><FaPlus />   Crear Función</button>
      </div>
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Película</th>
          <th>Sala</th>
          <th>Fecha</th>
          <th>Hora</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((f) => (
          <tr key={f.id}>
            <td>{f.id}</td>
            <td>{f.pelicula_id}</td>
            <td>{f.sala_id}</td>
            <td>{f.fecha}</td>
            <td>{f.hora}</td>
            {renderAcciones(f)}
          </tr>
        ))}
      </tbody>
    </table>
    </>
  );

  const renderReservaciones = () => (
    <>
      <div className="acciones-superiores">
        <button className="btn crear-grande"><FaPlus />   Crear Reservación</button>
      </div>
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Usuario</th>
          <th>Función</th>
          <th>Fila</th>
          <th>Columna</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {datos.map((r) => (
          <tr key={r.id}>
            <td>{r.id}</td>
            <td>{r.usuario_id}</td>
            <td>{r.funcion_id}</td>
            <td>{r.fila}</td>
            <td>{r.columna}</td>
            {renderAcciones(r)}
          </tr>
        ))}
      </tbody>
    </table>
    </>
  );

  const renderContenido = () => {
    switch (seccion) {
      case 'usuarios': return renderUsuarios();
      case 'peliculas': return renderPeliculas();
      case 'salas': return renderSalas();
      case 'funciones': return renderFunciones();
      case 'reservaciones': return renderReservaciones();
      default:
        return (
          <>
            <h1>Panel de Control</h1>
            <p>Selecciona una opción del menú lateral para comenzar.</p>
          </>
        );
    }
  };

  return (
    <div className="admin-dashboard">
      <aside className="sidebar">
        <h2 className="sidebar-title">Administración</h2>
        <nav className="sidebar-menu">
          <a onClick={() => setSeccion('usuarios')}><FaUserCog /> Usuarios</a>
          <a onClick={() => setSeccion('peliculas')}><FaFilm /> Películas</a>
          <a onClick={() => setSeccion('salas')}><FaDoorOpen /> Salas</a>
          <a onClick={() => setSeccion('funciones')}><FaCalendarAlt /> Funciones</a>
          <a onClick={() => setSeccion('reservaciones')}><FaClipboardList /> Reservaciones</a>
        </nav>
      </aside>

      <main className="dashboard-content">
        {renderContenido()}
      </main>
    </div>
  );
}

export default AdminDashboard;
