import '../actions_pages_styles/pelicula_editar.css';
import { FaFilm, FaClock, FaAlignLeft, FaImage } from 'react-icons/fa';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function PeliculaEditar() {
  const [titulo, setTitulo] = useState('');
  const [duracion, setDuracion] = useState('');
  const [sinopsis, setSinopsis] = useState('');
  const [poster, setPoster] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const obtenerPelicula = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get(`http://localhost:3000/api/peliculas/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTitulo(res.data.titulo);
      setDuracion(res.data.duracion);
      setSinopsis(res.data.sinopsis);
      setPoster(res.data.poster);
    };
    obtenerPelicula();
  }, [id]);

  const handleEditar = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:3000/api/peliculas/${id}`, {
        titulo,
        duracion,
        sinopsis,
        poster
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Película actualizada correctamente');
      navigate('/AdminDashboard');
    } catch (error) {
      alert('Error al actualizar película');
    }
  };

  return (
    <div className="form-page">
      <div className="form-card">
        <h2>Editar Película</h2>

        <div className="form-field">
          <FaFilm className="form-icon" />
          <input type="text" placeholder="Título" value={titulo} onChange={(e) => setTitulo(e.target.value)} />
        </div>

        <div className="form-field">
          <FaClock className="form-icon" />
          <input type="number" placeholder="Duración (minutos)" value={duracion} onChange={(e) => setDuracion(e.target.value)} />
        </div>

        <div className="form-field">
          <FaAlignLeft className="form-icon" />
          <textarea placeholder="Sinopsis" value={sinopsis} onChange={(e) => setSinopsis(e.target.value)} />
        </div>

        <div className="form-field">
          <FaImage className="form-icon" />
          <input type="text" placeholder="URL del póster" value={poster} onChange={(e) => setPoster(e.target.value)} />
        </div>

        <button className="form-button" onClick={handleEditar}>Guardar Cambios</button>
      </div>
    </div>
  );
}

export default PeliculaEditar;
