import { useEffect, useState } from 'react';
import axios from 'axios';
import './Cartelera.css';

function Cartelera() {
  const [peliculas, setPeliculas] = useState([]);

  useEffect(() => {
    const obtenerPeliculas = async () => {
      try {
        const res = await axios.get('http://localhost:3000/api/peliculas');
        setPeliculas(res.data.peliculas);
      } catch (error) {
        console.error('Error al cargar las películas:', error);
      }
    };

    obtenerPeliculas();
  }, []);

  return (
    <div className="contenedor">
      <h1 className="titulo">Cartelera</h1>
      <div className="contenedor-conciertos">
        {peliculas.map((pelicula) => (
          <div
            className="card"
            key={pelicula.id}
            style={{ backgroundImage: `url('${pelicula.poster_url}')` }}
          >
            <div className="overlay">
              <h3>{pelicula.titulo}</h3>
              <p>{pelicula.sinopsis}</p>
              <button className="boton">Comprar Entrada</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Cartelera;
